/*Do not copy my code*/public class Assignment2{public Assignment2(){}public static void main(String[] args){int[] a = {20, 4, 114, 23, 34, 25, 45, 66, 77, 89, 11};if(searchAlgorithm(a, 77) >= 0){System.out.println("Key is found at index " + searchAlgorithm(a, 77));System.out.println("Number of comparisons is " + (searchAlgorithm(a, 77) + 1));}else{System.out.println("Key not found");System.out.println("Number of comparisons is " + a.length);}}public static int searchAlgorithm(int[] a, int k){for(int i = 0; i < a.length; i++){if(a[i] == k)return i;}return -1;}public static int searchAlgorithm(int[] a, int k, int index){if(index >= a.length)return -1;if(a[index] == k)return index;return searchAlgorithm(a, k, index + 1);}}